mapper.select:
select ks_ku.te_name,ex_full_input_fu.in_subject,ex_full_input_fu.in_level from ks_ku
   join ex_full_input_fu on ks_ku.full_id=ex_full_input_fu.id
where ks_ku.tiku_id=#{id}

select ks_ku.te_name,ex_listen_fu.li_subject,ex_listen_fu.li_level from ks_ku
         join ex_listen_fu on ks_ku.listen_id=ex_listen_fu.id
where ks_ku.tiku_id=#{id}

select ks_ku.te_name,ex_yue_fu.yue_subject,ex_yue_fu.yue_level from ks_ku
      join ex_yue_fu on ks_ku.yue_id=ex_yue_fu.id
where ks_ku.tiku_id=#{id}
